import React, { useState } from 'react';
import axios from 'axios'; // Import Axios for making HTTP requests

function Formulaire() {
  const [formData, setFormData] = useState({
    restaurantName: '',
    cuisineType: '',
    address: '',
    phoneNumber: '',
    email: '',
    openingHours: '',
    deliveryAreas: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Send POST request to server with restaurant data
      const response = await axios.post('/restaurants', formData);
      console.log(response.data); // Log response from the server
      // Reset form after successful submission
      setFormData({
        restaurantName: '',
        cuisineType: '',
        address: '',
        phoneNumber: '',
        email: '',
        openingHours: '',
        deliveryAreas: '',
      });
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="max-w-md mx-auto p-6 bg-gray-100 rounded-lg shadow-md mt-4"> {/* Add mt-4 class here */}
      <h2 className="text-2xl font-bold mb-4">Restaurant Informations</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          {/* Your form inputs */}
        </div>
        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">Save</button>
      </form>
    </div>
  );
}

export default Formulaire;
