import React, { useState } from 'react';
import axios from 'axios';

function DeliveryAvailability() {
    const [address, setAddress] = useState('');
    const [deliveryAvailable, setDeliveryAvailable] = useState(null);

    const checkDeliveryAvailability = async () => {
        try {
            // Fetch restaurant addresses from MongoDB
            const response = await axios.get('/api/restaurants');
            const restaurants = response.data;

            // Calculate distance from user's address to each restaurant
            const distances = restaurants.map(restaurant => {
                return calculateDistance(address, restaurant.address);
            });

            // Check if any restaurant is within delivery range (e.g., within 10 miles)
            const withinDeliveryRange = distances.some(distance => distance <= 10); // Assume delivery range of 10 miles

            setDeliveryAvailable(withinDeliveryRange);
        } catch (error) {
            console.error('Error checking delivery availability:', error);
            setDeliveryAvailable(false); // Set to false in case of error
        }
    };

    const calculateDistance = (address1, address2) => {
        // Assuming address format: "latitude,longitude"
        const [lat1, lon1] = address1.split(',').map(parseFloat);
        const [lat2, lon2] = address2.split(',').map(parseFloat);

        const R = 6371; // Radius of the Earth in kilometers
        const dLat = (lat2 - lat1) * Math.PI / 180;
        const dLon = (lon2 - lon1) * Math.PI / 180;
        const a = Math.sin(dLat / 2) * Math.sin(dLat / 2) +
                  Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
                  Math.sin(dLon / 2) * Math.sin(dLon / 2);
        const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
        const distance = R * c; // Distance in kilometers

        return distance;
    };

    return (
        <div>
            <h2>Check Delivery Availability</h2>
            <input
                type="text"
                value={address}
                onChange={(e) => setAddress(e.target.value)}
                placeholder="Enter your address"
            />
            <button onClick={checkDeliveryAvailability}>Check Delivery</button>
            {deliveryAvailable !== null && (
                <p>
                    Delivery {deliveryAvailable ? 'is available' : 'is not available'} to {address}.
                </p>
            )}
        </div>
    );
}

export default DeliveryAvailability;
