import React, { useState } from 'react';
import axios from 'axios';
import backgroundImage from './images/photo1.jpg';


function DeliveryAvailability() {
    const [address, setAddress] = useState('');
    const [deliveryAvailable, setDeliveryAvailable] = useState(null);

    const checkDeliveryAvailability = async () => {
        try {
            // Make a POST request to check delivery availability based on user input
            const response = await axios.post('/check-delivery-availability', { userAddress: address });
            const { deliveryAvailable } = response.data;

            setDeliveryAvailable(deliveryAvailable);
        } catch (error) {
            console.error('Error checking delivery availability:', error);
            setDeliveryAvailable(false); // Set to false in case of error
        }
    };

    return (
      <div className="relative">
      <div
        className="absolute inset-0 bg-cover bg-center "
        style={{
          backgroundImage: `url(${backgroundImage})`,
          
          filter: 'blur(3px)', // Apply blur effect
          WebkitFilter: 'blur(8px)', // For Safari support
        }}
      />
        <div className="max-w-md mx-auto p-6 bg-gray-100 rounded-lg shadow-md mt-10">
            <h2 className="text-2xl font-bold mb-4 text-center">Check Delivery Availability</h2>
            <div className="mb-4">
                <label className="block mb-1">Enter your address</label>
                <input
                    type="text"
                    value={address}
                    onChange={(e) => setAddress(e.target.value)}
                    className="w-full border rounded px-3 py-2 mb-4" // Add mb-4 for margin bottom
                />
                <button className="block mx-auto bg-orange-400 text-white px-4 py-2 rounded hover:bg-orange-400" onClick={checkDeliveryAvailability}>Check Delivery</button>
                {/* {deliveryAvailable !== null && (
                    <p>
                        Delivery {deliveryAvailable ? 'is available' : 'is not available'} to {address}.
                    </p>
                )} */}
            </div>
        </div>
        </div>
    );
}

export default DeliveryAvailability;
