// ScrollablePhotoGallery.js
import React, { useRef, useEffect } from 'react';
import Photo1 from './images/photo1.jpg';
import Photo2 from './images/photo2.jpg';
import Photo3 from './images/photo3.jpg';

const ScrollablePhotoGallery = () => {
  const galleryRef = useRef(null);

  useEffect(() => {
    const intervalId = setInterval(() => {
      console.log("Scrolling");
      if (galleryRef.current) {
        galleryRef.current.scrollTo({
          left: galleryRef.current.scrollLeft + galleryRef.current.offsetWidth,
          behavior: 'smooth'
        });
      }
    }, 3000); // Adjust the interval (in milliseconds) for auto-scrolling

    return () => clearInterval(intervalId);
  }, []);

  // Array of imported photo modules
  const photos = [Photo1, Photo2, Photo3];

  return (
    <div ref={galleryRef} className="scrollable-photo-gallery p-4 overflow-x-auto">
      <h2 className="text-2xl font-bold mb-4">Photo Gallery</h2>
      <div className="flex">
        {photos.map((photo, index) => (
          <img key={index} src={photo} alt={`Photo ${index + 1}`} className="max-w-xs mr-4" />
        ))}
      </div>
    </div>
  );
};

export default ScrollablePhotoGallery;
