import React, { useState, useEffect } from 'react';
import backgroundImage from '../images/photo1.jpg';

function BusinessInsights() {
  const [restaurantData, setRestaurantData] = useState([]);

  useEffect(() => {
    fetch('/api/restaurantData')
      .then(response => response.json())
      .then(data => setRestaurantData(data))
      .catch(error => console.error('Error fetching restaurant data', error));
  }, []);

  return (
    <div className="relative">
      <div
        className="absolute inset-0 bg-cover bg-center"
        style={{
          backgroundImage: `url(${backgroundImage})`,
          zIndex: -1,
          filter: 'blur(4px)', 
          WebkitFilter: 'blur(8px)', 
        }}
      />
    <div className="mt-7 text-center">
      <h2 className="text-2xl font-bold mb-4">Restaurants dashboard</h2>
      <table className="border-collapse mx-auto rounded-lg overflow-hidden">
        <thead>
          <tr>
            <th className="border px-4 py-2 bg-yellow-300 text-white">Restaurant Name</th> 
            <th className="border px-4 py-2 bg-yellow-300 text-white">Cuisine Type</th>
            <th className="border px-4 py-2 bg-yellow-300 text-white">Address</th>
            <th className="border px-4 py-2 bg-yellow-300 text-white">Phone Number</th>
            <th className="border px-4 py-2 bg-yellow-300 text-white">Email</th>
            <th className="border px-4 py-2 bg-yellow-300 text-white">Opening Hours</th>
            <th className="border px-4 py-2 bg-yellow-300 text-white">Delivery Areas</th>
          </tr>
        </thead>
        <tbody>
          {restaurantData.map(restaurant => (
            <tr key={restaurant.id}>
              <td className="border px-4 py-2 text-white">{restaurant.restaurantName}</td>
              <td className="border px-4 py-2 text-white">{restaurant.cuisineType}</td>
              <td className="border px-4 py-2 text-white">{restaurant.address}</td>
              <td className="border px-4 py-2 text-white">{restaurant.phoneNumber}</td>
              <td className="border px-4 py-2 text-white">{restaurant.email}</td>
              <td className="border px-4 py-2 text-white">{restaurant.openingHours}</td>
              <td className="border px-4 py-2 text-white">{restaurant.deliveryAreas}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
    </div>
  );
}

export default BusinessInsights;
