import React, { useState } from 'react';
import axios from 'axios'; 
import backgroundImage from '../images/photo1.jpg';

function Formulaire() {
  const [formData, setFormData] = useState({
    restaurantName: '',
    cuisineType: '',
    address: '',
    phoneNumber: '',
    email: '',
    openingHours: '',
    deliveryAreas: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
    
      const response = await axios.post('/restaurants', formData);
      console.log(response.data); 
     
      setFormData({
        restaurantName: '',
        cuisineType: '',
        address: '',
        phoneNumber: '',
        email: '',
        openingHours: '',
        deliveryAreas: '',
      });
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="relative">
      <div
        className="absolute inset-0 bg-cover bg-center"
        style={{
          backgroundImage: `url(${backgroundImage})`,
          zIndex: -1,
          filter: 'blur(4px)', 
          WebkitFilter: 'blur(8px)', // For Safari support
        }}
      />
    <div className="max-w-md mx-auto p-6 bg-gray-100 rounded-lg shadow-md mt-7">
      
      <h2 className="text-2xl font-bold mb-4 text-center">Restaurant Informations</h2>
      
      <form onSubmit={handleSubmit}>
      <div className="mb-4">
          <label className="block mb-1 ">Restaurant Name</label>
          <input
            type="text"
            name="restaurantName"
            value={formData.restaurantName}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
          />
          <label className="block mb-1">Cuisine Type</label>
          <input
            type="text"
            name="cuisineType"
            value={formData.cuisineType}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
          />
          <label className="block mb-1">Address</label>
          <input
            type="text"
            name="address"
            value={formData.address}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
          />
          <label className="block mb-1">Phone Number</label>
          <input
            type="text"
            name="phoneNumber"
            value={formData.phoneNumber}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
          />
          <label className="block mb-1">Email</label>
          <input
            type="text"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
          />
          <label className="block mb-1">Opening Hours</label>
          <input
            type="text"
            name="openingHours"
            value={formData.openingHours}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
          />
          <label className="block mb-1">Delivery Areas</label>
          <input
            type="text"
            name="deliveryAreas"
            value={formData.deliveryAreas}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
          />
        </div>
        <button type="submit" className="block mx-auto bg-orange-400 text-white px-4 py-2 rounded hover:bg-orange-400">Save</button>
      </form>
    </div>
    </div>
  );
}

export default Formulaire;
