// ScrollablePhotoGallery.js
import React from 'react';
import Photo1 from './images/photo1.jpg';
import Photo2 from './images/photo2.webp';
import Photo3 from './images/photo3.webp';
import './ScrollablePhotoGallery.css'; // Import CSS file for animations

const ScrollablePhotoGallery = () => {
  return (
    <div className="scrollable-photo-gallery p-4">
      <h2 className="text-2xl font-bold mb-4">Photo Gallery</h2>
      <div className="flex overflow-hidden">
        <div className="scrolling-wrapper">
          <img src={Photo1} alt="Photo 1" className="max-w-xs mr-4" />
          <img src={Photo2} alt="Photo 2" className="max-w-xs mr-4" />
          <img src={Photo3} alt="Photo 3" className="max-w-xs mr-4" />
        </div>
      </div>
    </div>
  );
};

export default ScrollablePhotoGallery;
