import React, { useState } from 'react';
import axios from 'axios'; 

function Formulaire() {
  const [formData, setFormData] = useState({
    restaurantName: '',
    cuisineType: '',
    phoneNumber: '',
    latitude: '',
    longitude: '',
    deliveryDistance: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/restaurants', formData);
      console.log(response.data); 
     
      setFormData({
        restaurantName: '',
        cuisineType: '',
        phoneNumber: '',
        latitude: '',
        longitude: '',
        deliveryDistance: '',
      });
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className="flex justify-center gap-4">
      <div>
        <h2 className="text-2xl font-bold mb-4">Business Information</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block mb-1">Restaurant Name</label>
            <input
              type="text"
              name="restaurantName"
              value={formData.restaurantName}
              onChange={handleChange}
              className="w-96 border rounded px-3 py-2"
            />
            <label className="block mb-1">Cuisine Type</label>
            <input
              type="text"
              name="cuisineType"
              value={formData.cuisineType}
              onChange={handleChange}
              className="w-96 border rounded px-3 py-2"
            />
            <label className="block mb-1">Phone Number</label>
            <input
              type="text"
              name="phoneNumber"
              value={formData.phoneNumber}
              onChange={handleChange}
              className="w-96 border rounded px-3 py-2"
            />
          </div>
        </form>
      </div>
      <div>
        <h2 className="text-2xl font-bold mb-4">Address</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block mb-1">Latitude</label>
            <input
              type="text"
              name="latitude"
              value={formData.latitude}
              onChange={handleChange}
              className="w-96 border rounded px-3 py-2"
            />
            <label className="block mb-1">Longitude</label>
            <input
              type="text"
              name="longitude"
              value={formData.longitude}
              onChange={handleChange}
              className="w-96 border rounded px-3 py-2"
            />
            <label className="block mb-1">Delivery Distance (Km)</label>
            <input
              type="text"
              name="deliveryDistance"
              value={formData.deliveryDistance}
              onChange={handleChange}
              className="w-96 border rounded px-3 py-2"
            />
          </div>
          <button type="submit" className="block mx-auto px-4 py-2 rounded" style={{ backgroundColor: '#E9E9E9', color: '#333' }}>Save</button>
        </form>
      </div>
    </div>
  );
}

export default Formulaire;
