import React, { useState } from 'react';

const DeliveryAvailability = () => {
  const [userLocation, setUserLocation] = useState('');
  const [restaurants, setRestaurants] = useState([]);

  // Fonction pour gérer la soumission du formulaire d'emplacement utilisateur
  const handleSubmit = (e) => {
    e.preventDefault();
    // Appel à votre backend pour récupérer les restaurants disponibles en fonction de l'emplacement de l'utilisateur
    fetch(`your-backend-url/api/restaurants?location=${userLocation}`)
      .then(response => response.json())
      .then(data => setRestaurants(data));
  }

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Entrez votre emplacement"
          value={userLocation}
          onChange={(e) => setUserLocation(e.target.value)}
        />
        <button type="submit">Vérifier la disponibilité</button>
      </form>
      <div>
        {restaurants.map(restaurant => (
          <div key={restaurant.id}>
            <h3>{restaurant.restaurantName}</h3>
            <p>Distance: {restaurant.address} km</p>
            {/* Ajoutez d'autres détails du restaurant selon vos besoins */}
          </div>
        ))}
      </div>
    </div>
  );
}

export default DeliveryAvailability;
