import React, { useState, useEffect } from 'react';

function BusinessInsights() {
  const [restaurantData, setRestaurantData] = useState([]);

  useEffect(() => {
    // API call to fetch restaurant data
    fetch('/api/restaurantData')
      .then(response => response.json())
      .then(data => setRestaurantData(data))
      .catch(error => console.error('Error fetching restaurant data', error));
  }, []);

  return (
    <div className="mt-7 text-center">
      <h2 className="text-2xl font-bold mb-4">Business Information Dashboard</h2>
      <table className="border-collapse mx-auto rounded-lg overflow-hidden">
        <thead>
          <tr>
            <th className="px-4 py-2 bg-green-300 text-white">Restaurant Name</th> {/* Change header color */}
            <th className="px-4 py-2 bg-green-300 text-white">Cuisine Type</th>
            <th className="px-4 py-2 bg-green-300 text-white">Address</th>
            <th className="px-4 py-2 bg-green-300 text-white">Phone Number</th>
            <th className="px-4 py-2 bg-green-400 text-white">Email</th>
            <th className="px-4 py-2 bg-green-400 text-white">Opening Hours</th>
            <th className="px-4 py-2 bg-green-400 text-white">Delivery Areas</th>
          </tr>
        </thead>
        <tbody>
          {restaurantData.map(restaurant => (
            <tr key={restaurant.id}>
              <td className="border px-4 py-2">{restaurant.restaurantName}</td>
              <td className="border px-4 py-2">{restaurant.cuisineType}</td>
              <td className="border px-4 py-2">{restaurant.address}</td>
              <td className="border px-4 py-2">{restaurant.phoneNumber}</td>
              <td className="border px-4 py-2">{restaurant.email}</td>
              <td className="border px-4 py-2">{restaurant.openingHours}</td>
              <td className="border px-4 py-2">{restaurant.deliveryAreas}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default BusinessInsights;
