import React, { useState, useEffect } from 'react';
import { PieChart, Pie, Cell, ResponsiveContainer, Legend } from 'recharts';

function BusinessInsights() {
  const [restaurantData, setRestaurantData] = useState([]);

  useEffect(() => {
    // Fetch restaurant data from the API
    fetch('/api/restaurantData')
      .then(response => response.json())
      .then(data => setRestaurantData(data))
      .catch(error => console.error('Error fetching restaurant data:', error));
  }, []);

  // Function to calculate distribution of restaurant types
  const calculateRestaurantTypeDistribution = () => {
    const typeCount = {};
    restaurantData.forEach(restaurant => {
      typeCount[restaurant.cuisineType] = (typeCount[restaurant.cuisineType] || 0) + 1;
    });
    return Object.entries(typeCount).map(([type, count]) => ({ name: type, value: count }));
  };

  // Data for the pie chart
  const data = calculateRestaurantTypeDistribution();

  // Colors for the pie chart
  const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#AF19FF'];

  return (
    <div>
      <h2>Business Information Dashboard</h2>
      {/* Display table of restaurant data */}
      <table>
        <thead>
          <tr>
            <th>Restaurant Name</th>
            <th>Cuisine Type</th>
            <th>Address</th>
            <th>Phone Number</th>
            <th>Email</th>
            <th>Opening Hours</th>
            <th>Delivery Areas</th>
          </tr>
        </thead>
        <tbody>
          {restaurantData.map(restaurant => (
            <tr key={restaurant.id}>
              <td>{restaurant.restaurantName}</td>
              <td>{restaurant.cuisineType}</td>
              <td>{restaurant.address}</td>
              <td>{restaurant.phoneNumber}</td>
              <td>{restaurant.email}</td>
              <td>{restaurant.openingHours}</td>
              <td>{restaurant.deliveryAreas}</td>
            </tr>
          ))}
        </tbody>
      </table>
      
      {/* Display pie chart of restaurant type distribution */}
      <div style={{ width: '100%', height: 300 }}>
        <ResponsiveContainer>
          <PieChart>
            <Pie
              data={data}
              dataKey="value"
              nameKey="name"
              cx="50%"
              cy="50%"
              outerRadius={80}
              fill="#8884d8"
              label
            >
              {data.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
              ))}
            </Pie>
            <Legend />
          </PieChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
}

export default BusinessInsights;
