import React, { useState, useEffect } from 'react';

function BusinessInsights() {
  const [restaurantData, setRestaurantData] = useState([]);
  const [searchPhone, setSearchPhone] = useState('');

  useEffect(() => {
    fetch('/api/restaurantData')
      .then(response => response.json())
      .then(data => setRestaurantData(data))
      .catch(error => console.error('Error fetching restaurant data', error));
  }, []);

  // Filter restaurants based on the entered phone number
  const filteredRestaurants = restaurantData.filter(restaurant =>
    restaurant.phoneNumber.includes(searchPhone)
  );

  // Function to determine the color based on cuisine type
  const getCuisineColor = cuisineType => {
    switch (cuisineType) {
      case 'Italian':
        return 'lightgreen';
      case 'Indian':
        return 'lightcoral';
      case 'Mexican':
        return 'lightyellow';
      default:
        return 'transparent';
    }
  };

  return (
    <div className="mt-7 text-center">
      {/* Your existing JSX */}
      <table className="border-collapse mx-auto rounded-lg overflow-hidden mt-4">
        <thead>
          <tr>
            <th className="border px-4 py-2 bg-gray-200 text-black font-normal">Restaurant Name</th>
            <th className="border px-4 py-2 bg-gray-200 text-black font-normal">Cuisine Type</th>
            <th className="border px-4 py-2 bg-gray-200 text-black font-normal">Phone Number</th>
            <th className="border px-4 py-2 bg-gray-200 text-black font-normal">Longitude</th>
            <th className="border px-4 py-2 bg-gray-200 text-black font-normal">Latitude</th>
            <th className="border px-4 py-2 bg-gray-200 text-black font-normal">Delivery Distance</th>
          </tr>
        </thead>
        <tbody>
          {filteredRestaurants.map(restaurant => (
            <tr key={restaurant.id}>
              <td className="border px-4 py-2 text-black">{restaurant.restaurantName}</td>
              {/* Apply inline style to the cuisineType cell */}
              <td className="border px-4 py-2 text-black" style={{ border: `3px solid ${getCuisineColor(restaurant.cuisineType)}` }}>
                {restaurant.cuisineType}
              </td>
              <td className="border px-4 py-2 text-black">{restaurant.phoneNumber}</td>
              <td className="border px-4 py-2 text-black">{restaurant.longitude}</td>
              <td className="border px-4 py-2 text-black">{restaurant.latitude}</td>
              <td className="border px-4 py-2 text-black">{restaurant.deliveryDistance}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default BusinessInsights;
