import React, { useState, useEffect } from 'react';
import backgroundImage from '../images/photo1.jpg';

function BusinessInsights() {
  const [restaurantData, setRestaurantData] = useState([]);
  const [searchPhone, setSearchPhone] = useState('');

  useEffect(() => {
    fetch('/api/restaurantData')
      .then(response => response.json())
      .then(data => setRestaurantData(data))
      .catch(error => console.error('Error fetching restaurant data', error));
  }, []);

  // Filter restaurants based on the entered phone number
  const filteredRestaurants = restaurantData.filter(restaurant =>
    restaurant.phoneNumber.includes(searchPhone)
  );

  return (
    <div className="relative">
      <div
        className="absolute inset-0 bg-cover bg-center"
        style={{
          backgroundImage: `url(${backgroundImage})`,
          zIndex: -1,
          filter: 'blur(4px)', 
          WebkitFilter: 'blur(8px)', 
        }}
      />
      <div className="mt-7 text-center">
        <h2 className="text-2xl font-bold mb-4">Restaurants Dashboard</h2>
        <div className="mb-4">
          <label htmlFor="phoneSearch" className="mr-2">Search by Phone:</label>
          <input
            type="text"
            id="phoneSearch"
            value={searchPhone}
            onChange={e => setSearchPhone(e.target.value)}
            className="border rounded px-3 py-2"
          />
        </div>
        <table className="border-collapse mx-auto rounded-lg overflow-hidden">
          <thead>
            <tr>
              <th className="border px-4 py-2 bg-yellow-300 text-white">Restaurant Name</th> 
              <th className="border px-4 py-2 bg-yellow-300 text-white">Cuisine Type</th>
              <th className="border px-4 py-2 bg-yellow-300 text-white">Phone Number</th>
              <th className="border px-4 py-2 bg-yellow-300 text-white">Longitude</th>
              <th className="border px-4 py-2 bg-yellow-300 text-white">Latitude</th>
              <th className="border px-4 py-2 bg-yellow-300 text-white">Delivery Distance</th>
            </tr>
          </thead>
          <tbody>
            {filteredRestaurants.map(restaurant => (
              <tr key={restaurant.id}>
                <td className="border px-4 py-2 text-white">{restaurant.restaurantName}</td>
                <td className="border px-4 py-2 text-white">{restaurant.cuisineType}</td>
                <td className="border px-4 py-2 text-white">{restaurant.phoneNumber}</td>
                <td className="border px-4 py-2 text-white">{restaurant.longitude}</td>
                <td className="border px-4 py-2 text-white">{restaurant.latitude}</td>
                <td className="border px-4 py-2 text-white">{restaurant.deliveryDistance}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default BusinessInsights;
