import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Dashboard() {
    const [restaurants, setRestaurants] = useState([]);

    useEffect(() => {
        // Retrieve data from the server when the component mounts
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            // Make a GET request to retrieve the stored restaurant data
            const response = await axios.get('/restaurants');
            setRestaurants(response.data);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    const calculateRestaurantDistribution = () => {
        // Calculate the distribution of restaurant types
        const distribution = restaurants.reduce((acc, restaurant) => {
            acc[restaurant.cuisineType] = (acc[restaurant.cuisineType] || 0) + 1;
            return acc;
        }, {});
        return distribution;
    };

    return (
        <div className="container mx-auto">
            <h2 className="text-2xl font-bold mb-4 text-center">Restaurant Insights</h2>
            <div className="grid grid-cols-2 gap-4">
                <div>
                    <h3 className="text-xl font-bold mb-2">Restaurant Types</h3>
                    <ul>
                        {Object.entries(calculateRestaurantDistribution()).map(([type, count]) => (
                            <li key={type}>{type}: {count}</li>
                        ))}
                    </ul>
                </div>
                {/* Add more insights as needed */}
            </div>
        </div>
    );
}

export default Dashboard;
