// ScrollablePhotoGallery.js
import React from 'react';
import Photo1 from './images/photo1.jpg';
import Photo2 from './images/photo2.webp';
import Photo3 from './images/photo3.webp';

const ScrollablePhotoGallery = () => {
  // Array of imported photo modules
  const photos = [Photo1, Photo2, Photo3];

  return (
    <div className="scrollable-photo-gallery">
      <h2>Photo Gallery</h2>
      <div className="photo-container">
        {photos.map((photo, index) => (
          <img key={index} src={photo} alt={`Photo ${index + 1}`} />
        ))}
      </div>
    </div>
  );
};

export default ScrollablePhotoGallery;
